package ciclo_while;

public class cwhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//ciclo while
		
		System.out.println("Hola mundo");
		int i = 1;
		while(i < 10) {
			System.out.println("Hola mundo");
			i++;
		}
		
		
		
		//ciclo do-while
		
		int num = 0;
		do{
			System.out.println("Hola");
			num++;
		}while(num < 10);
		
		
		//ciclo for
		
		for(int j = 0; j < 10; j++){
			System.out.println("Nachi te amo");
			
		}
		
	}

}
